import React, { useState } from 'react';

const FeedbackForm = ({ mentorId, userId, mentorName }) => {
  const [feedbackText, setFeedbackText] = useState('');
  const [rating, setRating] = useState(0);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:5000/api/feedback', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ mentorId, userId, feedbackText, rating, mentorName }),
    });
    const data = await response.json();
    console.log(data.message);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <textarea
        value={feedbackText}
        onChange={(e) => setFeedbackText(e.target.value)}
        placeholder="Enter your feedback"
        required
        className="w-full border rounded p-2"
      />
      <div className="flex items-center">
        {[1, 2, 3, 4, 5].map((star) => (
          <span key={star} onClick={() => setRating(star)} className={`cursor-pointer text-${rating >= star ? 'yellow' : 'gray'}-500 text-3xl`}>
            â˜…
          </span>
        ))}
      </div>
      <button type="submit" className="w-full bg-yellow-500 text-black py-2 rounded-md hover:bg-yellow-600 transition duration-300">Submit Feedback</button>
    </form>
  );
};

export default FeedbackForm;
