import React from 'react';
import { Link } from 'react-router-dom';
import { FaSearch, FaUsers, FaGraduationCap } from 'react-icons/fa';
import Hero from './Hero';
import FeaturedMentors from './FeaturedMentors';
import HackathonList from './HackathonList';

const Home = () => {
  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <Hero />

      {/* How It Works Section */}
      <div className="py-16 bg-white">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold text-center mb-12">How It Works</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <div className="text-center p-6">
              <div className="flex justify-center mb-4">
                <FaSearch className="text-blue-600 text-4xl" />
              </div>
              <h3 className="text-xl font-semibold mb-2">Find Your Mentor</h3>
              <p className="text-gray-600">
                Browse through our curated list of experienced mentors and find the perfect match for your needs.
              </p>
            </div>
            <div className="text-center p-6">
              <div className="flex justify-center mb-4">
                <FaUsers className="text-blue-600 text-4xl" />
              </div>
              <h3 className="text-xl font-semibold mb-2">Connect</h3>
              <p className="text-gray-600">
                Schedule sessions, chat with mentors, and get personalized guidance for your projects.
              </p>
            </div>
            <div className="text-center p-6">
              <div className="flex justify-center mb-4">
                <FaGraduationCap className="text-blue-600 text-4xl" />
              </div>
              <h3 className="text-xl font-semibold mb-2">Learn & Grow</h3>
              <p className="text-gray-600">
                Gain valuable insights, develop new skills, and accelerate your career growth.
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Featured Mentors Section */}
      <FeaturedMentors />

      {/* Hackathon List Section */}
      <HackathonList />
    </div>
  );
};

export default Home;
