import React, { useState } from 'react';

const mockMentors = [
  {
    id: 1,
    name: 'Sarah Johnson',
    expertise: ['React', 'Node.js', 'MongoDB'],
    experience: '5+ years',
    availability: 'Weekends',
    rating: 4.8,
    imageUrl: 'https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=2&w=256&h=256&q=80',
  },
  {
    id: 2,
    name: 'Michael Chen',
    expertise: ['Python', 'Machine Learning', 'AWS'],
    experience: '7+ years',
    availability: 'Evenings',
    rating: 4.9,
    imageUrl: 'https://images.unsplash.com/photo-1519244703995-f4e0f30006d5?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=2&w=256&h=256&q=80',
  },
  // Add more mock mentors as needed
];

const expertiseOptions = ['React', 'Node.js', 'Python', 'Machine Learning', 'AWS', 'MongoDB', 'JavaScript', 'Data Science'];
const availabilityOptions = ['Weekdays', 'Weekends', 'Evenings', 'Flexible'];

function MentorSearch() {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedExpertise, setSelectedExpertise] = useState([]);
  const [selectedAvailability, setSelectedAvailability] = useState('');
  const [filteredMentors, setFilteredMentors] = useState(mockMentors);

  const handleSearch = () => {
    const filtered = mockMentors.filter(mentor => {
      const matchesSearch = mentor.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        mentor.expertise.some(exp => exp.toLowerCase().includes(searchTerm.toLowerCase()));

      const matchesExpertise = selectedExpertise.length === 0 ||
        selectedExpertise.some(exp => mentor.expertise.includes(exp));

      const matchesAvailability = !selectedAvailability ||
        mentor.availability === selectedAvailability;

      return matchesSearch && matchesExpertise && matchesAvailability;
    });

    setFilteredMentors(filtered);
  };

  const toggleExpertise = (expertise) => {
    setSelectedExpertise(prev =>
      prev.includes(expertise)
        ? prev.filter(e => e !== expertise)
        : [...prev, expertise]
    );
  };

  React.useEffect(() => {
    handleSearch();
  }, [searchTerm, selectedExpertise, selectedAvailability]);

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-gray-900 mb-4">Find Your Perfect Mentor</h2>
        <p className="text-gray-600">Connect with experienced mentors who can guide you through your hackathon journey.</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
        {/* Filters Section */}
        <div className="lg:col-span-1 space-y-6">
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Filters</h3>

            {/* Search Input */}
            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Search
              </label>
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Search by name or expertise..."
              />
            </div>

            {/* Expertise Filter */}
            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Expertise
              </label>
              <div className="space-y-2">
                {expertiseOptions.map((expertise) => (
                  <label key={expertise} className="flex items-center">
                    <input
                      type="checkbox"
                      checked={selectedExpertise.includes(expertise)}
                      onChange={() => toggleExpertise(expertise)}
                      className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                    />
                    <span className="ml-2 text-sm text-gray-600">{expertise}</span>
                  </label>
                ))}
              </div>
            </div>

            {/* Availability Filter */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Availability
              </label>
              <select
                value={selectedAvailability}
                onChange={(e) => setSelectedAvailability(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                <option value="">Any availability</option>
                {availabilityOptions.map((option) => (
                  <option key={option} value={option}>{option}</option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* Mentors Grid */}
        <div className="lg:col-span-3">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {filteredMentors.map((mentor) => (
              <div key={mentor.id} className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
                <div className="flex items-start space-x-4">
                  <img
                    src={mentor.imageUrl}
                    alt={mentor.name}
                    className="h-12 w-12 rounded-full"
                  />
                  <div className="flex-1">
                    <h3 className="text-lg font-semibold text-gray-900">{mentor.name}</h3>
                    <p className="text-sm text-gray-500">{mentor.experience} experience</p>
                    <div className="mt-2 flex flex-wrap gap-2">
                      {mentor.expertise.map((exp) => (
                        <span
                          key={exp}
                          className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800"
                        >
                          {exp}
                        </span>
                      ))}
                    </div>
                    <div className="mt-4 flex items-center justify-between">
                      <div className="flex items-center">
                        <span className="text-sm text-gray-500">{mentor.availability}</span>
                      </div>
                      <div className="flex items-center">
                        <svg className="h-5 w-5 text-yellow-400" fill="currentColor" viewBox="0 0 20 20">
                          <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                        </svg>
                        <span className="ml-1 text-sm text-gray-600">{mentor.rating}</span>
                      </div>
                    </div>
                    <button className="mt-4 w-full inline-flex justify-center items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500">
                      Connect
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default MentorSearch;
