import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { FaStar, FaLinkedin, FaGithub, FaGlobe } from 'react-icons/fa';

function MentorProfile() {
  const { id } = useParams();
  const [rating, setRating] = useState(0);
  const [hover, setHover] = useState(0);
  const [feedback, setFeedback] = useState('');
  const [activeTab, setActiveTab] = useState('ABOUT');
  const [mentor, setMentor] = useState(null);
  const [chatMessage, setChatMessage] = useState('');

  useEffect(() => {
    // In a real app, this would be an API call
    const mentors = [
      {
        id: '1',
        name: 'Rishabh Jain',
        title: 'Senior Software Engineer',
        rating: 4.9,
        reviews: 127,
        skills: ['React', 'Node.js', 'Python'],
        expertiseLevel: 'Expert',
        imageUrl: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-1.2.1&auto=format&fit=facearea&facepad=2&w=256&h=256&q=80',
        available: true,
        bio: 'Experienced Senior Software Engineer with a passion for teaching and mentoring. Specializing in React, Node.js, Python.',
        experience: [
          {
            role: 'Senior Software Engineer',
            company: 'Tech Corp',
            duration: '2020 - Present',
            description: 'Leading frontend development team and mentoring junior developers.'
          },
          {
            role: 'Software Engineer',
            company: 'StartupX',
            duration: '2018 - 2020',
            description: 'Full-stack development using React and Node.js.'
          }
        ],
        education: [
          {
            degree: 'M.S. Computer Science',
            school: 'Stanford University',
            year: '2018'
          },
          {
            degree: 'B.Tech Computer Science',
            school: 'IIT Delhi',
            year: '2016'
          }
        ],
        links: {
          linkedin: 'https://linkedin.com/in/rishabh-jain',
          github: 'https://github.com/rishabh',
          website: 'https://rishabh.dev'
        }
      },
      // Add other mentors here
    ];

    const foundMentor = mentors.find(m => m.id === id);
    setMentor(foundMentor);
  }, [id]);

  const handleSubmitFeedback = (e) => {
    e.preventDefault();
    // TODO: Implement feedback submission to backend
    console.log({ rating, feedback });
    setRating(0);
    setFeedback('');
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    // TODO: Implement chat functionality
    console.log({ message: chatMessage });
    setChatMessage('');
  };

  if (!mentor) return <div>Loading...</div>;

  const renderTabContent = () => {
    switch (activeTab) {
      case 'ABOUT':
        return <p className="text-gray-600">{mentor.bio}</p>;

      case 'EXPERIENCE':
        return (
          <div className="space-y-6">
            {mentor.experience.map((exp, index) => (
              <div key={index} className="border-l-2 border-gray-200 pl-4">
                <h4 className="text-lg font-semibold text-gray-900">{exp.role}</h4>
                <p className="text-sm text-gray-500">{exp.company} • {exp.duration}</p>
                <p className="mt-2 text-gray-600">{exp.description}</p>
              </div>
            ))}
          </div>
        );

      case 'EDUCATION':
        return (
          <div className="space-y-6">
            {mentor.education.map((edu, index) => (
              <div key={index} className="border-l-2 border-gray-200 pl-4">
                <h4 className="text-lg font-semibold text-gray-900">{edu.degree}</h4>
                <p className="text-sm text-gray-500">{edu.school} • {edu.year}</p>
              </div>
            ))}
          </div>
        );

      case 'CHAT':
        return (
          <div className="space-y-4">
            <div className="bg-gray-50 p-4 rounded-lg">
              <div className="space-y-4">
                {/* Chat messages would go here */}
                <div className="flex justify-start">
                  <div className="bg-white rounded-lg p-3 shadow-sm">
                    <p className="text-gray-600">Hello! How can I help you today?</p>
                  </div>
                </div>
              </div>
            </div>
            <form onSubmit={handleSendMessage} className="flex space-x-2">
              <input
                type="text"
                value={chatMessage}
                onChange={(e) => setChatMessage(e.target.value)}
                placeholder="Type your message..."
                className="flex-1 p-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              />
              <button
                type="submit"
                className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
              >
                Send
              </button>
            </form>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="bg-white rounded-lg shadow-sm">
        {/* Profile Header */}
        <div className="p-6">
          <div className="flex items-start justify-between">
            <div className="flex items-center space-x-4">
              <img
                src={mentor.imageUrl}
                alt={mentor.name}
                className="h-24 w-24 rounded-full"
              />
              <div>
                <h1 className="text-2xl font-bold text-gray-900">{mentor.name}</h1>
                <p className="text-gray-600">{mentor.title}</p>
                <div className="flex items-center mt-2">
                  {[...Array(5)].map((_, index) => (
                    <FaStar
                      key={index}
                      className={index < Math.floor(mentor.rating) ? 'text-yellow-400' : 'text-gray-300'}
                    />
                  ))}
                  <span className="ml-2 text-sm text-gray-600">({mentor.reviews} reviews)</span>
                </div>
                {mentor.available && (
                  <span className="inline-flex items-center px-3 py-1 mt-2 rounded-full text-sm font-medium bg-green-100 text-green-800">
                    Available for Mentoring
                  </span>
                )}
              </div>
            </div>

            {/* Connect Section */}
            <div className="w-72">
              <div className="bg-white rounded-lg border border-gray-200 p-4">
                <h3 className="text-lg font-medium mb-4">Connect</h3>
                <button className="w-full bg-blue-500 text-white rounded-md py-2 mb-4 hover:bg-blue-600">
                  Start Chat
                </button>
                <div className="space-y-2">
                  <a
                    href={mentor.links.linkedin}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="w-full border border-gray-300 rounded-md py-2 flex items-center justify-center space-x-2 hover:bg-gray-50"
                  >
                    <FaLinkedin className="h-5 w-5 text-blue-600" />
                    <span>LinkedIn</span>
                  </a>
                  <a
                    href={mentor.links.github}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="w-full border border-gray-300 rounded-md py-2 flex items-center justify-center space-x-2 hover:bg-gray-50"
                  >
                    <FaGithub className="h-5 w-5 text-gray-800" />
                    <span>GitHub</span>
                  </a>
                  <a
                    href={mentor.links.website}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="w-full border border-gray-300 rounded-md py-2 flex items-center justify-center space-x-2 hover:bg-gray-50"
                  >
                    <FaGlobe className="h-5 w-5 text-gray-600" />
                    <span>Website</span>
                  </a>
                </div>
              </div>

              <div className="mt-4 bg-white rounded-lg border border-gray-200 p-4">
                <h3 className="text-lg font-medium mb-2">Schedule a Session</h3>
                <p className="text-sm text-gray-600 mb-4">
                  Book a one-on-one mentoring session with {mentor.name}
                </p>
                <button className="w-full bg-purple-600 text-white rounded-md py-2 hover:bg-purple-700">
                  Book Session
                </button>
              </div>
            </div>
          </div>

          {/* Skills */}
          <div className="mt-6">
            <h3 className="text-lg font-medium mb-2">Skills</h3>
            <div className="flex flex-wrap gap-2">
              {mentor.skills.map((skill) => (
                <span
                  key={skill}
                  className="px-3 py-1 rounded-md text-sm font-medium bg-gray-100 text-gray-800"
                >
                  {skill}
                </span>
              ))}
            </div>
          </div>

          {/* Tabs */}
          <div className="mt-8 border-b border-gray-200">
            <nav className="-mb-px flex space-x-8">
              {['ABOUT', 'EXPERIENCE', 'EDUCATION', 'CHAT'].map((tab) => (
                <button
                  key={tab}
                  onClick={() => setActiveTab(tab)}
                  className={`pb-4 px-1 border-b-2 font-medium text-sm ${
                    tab === activeTab
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  {tab}
                </button>
              ))}
            </nav>
          </div>

          {/* Tab Content */}
          <div className="mt-6">
            {renderTabContent()}
          </div>
        </div>

        {/* Feedback Section */}
        <div className="border-t border-gray-200 mt-8 p-6">
          <h3 className="text-lg font-medium mb-4">Give Feedback</h3>
          <form onSubmit={handleSubmitFeedback}>
            <div className="flex mb-4">
              {[...Array(5)].map((_, index) => {
                const ratingValue = index + 1;
                return (
                  <label key={index}>
                    <input
                      type="radio"
                      name="rating"
                      className="hidden"
                      value={ratingValue}
                      onClick={() => setRating(ratingValue)}
                    />
                    <FaStar
                      className="cursor-pointer w-8 h-8"
                      color={ratingValue <= (hover || rating) ? "#ffc107" : "#e4e5e9"}
                      onMouseEnter={() => setHover(ratingValue)}
                      onMouseLeave={() => setHover(0)}
                    />
                  </label>
                );
              })}
            </div>
            <textarea
              className="w-full p-3 border border-gray-300 rounded-md"
              rows="4"
              placeholder="Write your feedback..."
              value={feedback}
              onChange={(e) => setFeedback(e.target.value)}
            />
            <button
              type="submit"
              className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
            >
              Submit Feedback
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default MentorProfile;
