import React, { useState } from 'react';

function MentorSearch() {
  const [searchTerm, setSearchTerm] = useState('');

  // Static mentor data for frontend-only mode
  const mentors = [
    {
      id: 1,
      name: 'Sarah Johnson',
      expertise: ['React', 'Node.js', 'MongoDB'],
      experience: '8 years',
      bio: 'Full-stack developer with a passion for teaching'
    },
    {
      id: 2,
      name: 'Michael Chen',
      expertise: ['Python', 'Machine Learning', 'Data Science'],
      experience: '6 years',
      bio: 'AI researcher and experienced hackathon mentor'
    },
    {
      id: 3,
      name: 'Emma Williams',
      expertise: ['UI/UX', 'Frontend Development', 'Design Systems'],
      experience: '5 years',
      bio: 'Design-focused developer helping teams create beautiful products'
    }
  ];

  const filteredMentors = mentors.filter(mentor =>
    mentor.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    mentor.expertise.some(skill => skill.toLowerCase().includes(searchTerm.toLowerCase())) ||
    mentor.bio.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <section className="py-12 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center">
          <h2 className="text-3xl font-extrabold text-gray-900 sm:text-4xl">
            Find Your Perfect Mentor
          </h2>
          <p className="mt-3 max-w-2xl mx-auto text-xl text-gray-500 sm:mt-4">
            Search for mentors by name, expertise, or skills
          </p>
        </div>

        <div className="mt-8 max-w-xl mx-auto">
          <div className="mt-1 relative rounded-md shadow-sm">
            <input
              type="text"
              className="focus:ring-indigo-500 focus:border-indigo-500 block w-full px-4 py-3 sm:text-sm border-gray-300 rounded-md"
              placeholder="Search mentors..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>

        <div className="mt-12 grid gap-8 sm:grid-cols-2 lg:grid-cols-3">
          {filteredMentors.map((mentor) => (
            <div
              key={mentor.id}
              className="bg-white overflow-hidden shadow rounded-lg hover:shadow-lg transition-shadow duration-300"
            >
              <div className="px-4 py-5 sm:p-6">
                <h3 className="text-lg font-medium text-gray-900">{mentor.name}</h3>
                <p className="mt-1 text-sm text-gray-500">{mentor.bio}</p>
                <div className="mt-4">
                  <div className="flex flex-wrap gap-2">
                    {mentor.expertise.map((skill, index) => (
                      <span
                        key={index}
                        className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-indigo-100 text-indigo-800"
                      >
                        {skill}
                      </span>
                    ))}
                  </div>
                </div>
                <div className="mt-4 text-sm text-gray-500">
                  Experience: {mentor.experience}
                </div>
                <div className="mt-6">
                  <button className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700">
                    Connect
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default MentorSearch;
